SELECT id, order_id, olorderno, stauts_ofline_hems, status_ofline_doms, timerShaftId, sapno, sappickno, last_timestamp, status, order_v, additional1, additional2, additional3, additional4, additional5, time_stamp, create_time, update_time, ext1, ext2, ext3, ext4, ext5
  FROM dst_111yao_doms.d_hems_in_order_status_17_h


        AND id = #{id}


